import React from 'react';
import FlashDeal from './components/FlashDeal/FlashDeal';

const App = () => {
    const addToCart = (product) => {
        // Xử lý thêm sản phẩm vào giỏ hàng ở đây
        console.log("Sản phẩm đã được thêm vào giỏ hàng:", product);
    };

    return (
        <div>
            <FlashDeal addToCart={addToCart} />
        </div>
    );
};

export default App;
