import React from 'react';
import { BrowserRouter as Route, Router } from 'react-router-dom';
import Header from './commom/header';
import Pages from './pages/pages';
import Cart from './commom/cart/Cart';
import {useState} from 'react';
import SData from './conponents/Shops/SData';
import Table from './conponents/Table/table';
import Footer from './commom/footer';
import Data from './conponents/FlashDeal/Data';

function App() {
  const {productItems} = Data
  const {shopItems} = SData

  const [cartItem, setCartItem] = useState([])

  const addToCart = (product) => {
    const productExit = cartItem.find((item) => item.id === product.id)

    if(productExit){
      setCartItem(cartItem.map((item) =>
       (item.id === product.id?
        {... productExit, qty:productExit.qty+1} 
        : item )))
    }
    else{
      setCartItem([... cartItem, {...product, qty:1}])
    }
  }

  const decreaseQty = (product) =>{
    const productExit = cartItem.find((item) => item.id === product.id)
    if(productExit.qty === 1){
      setCartItem(cartItem.filter((item) => item.id !== product.id))
    }else{
      setCartItem(cartItem.map((item) =>
       (item.id === product.id?
        {... productExit, qty:productExit.qty-1} 
        : item )))
    }
  }

  const removeItem = (product) =>{
    setCartItem(cartItem.filter((item) => item.id !== product.id))
  }

  return (


    <div className=""container mt-5>
      <Router>

      <Header cartItem={cartItem}/>



        <Route path='/' exact>
        <Pages productItems={productItems} addToCart={addToCart} shopItems={shopItems}  />
        </Route>

        <Route path='/table' element={<Table/>} />

        <Route path="/cart" exact>
              <Cart cartItem={cartItem} addToCart={addToCart} decreaseQty={decreaseQty} removeItem={removeItem} />
            </Route>


        <Footer/>
      </Router>

    </div>


  );
}

export default App;
