import React, { useState } from 'react';
import Slider from 'react-slick';

const NextArrow = (props) => {
    const { onClick } = props;
    return (
        <div className='control-btn' onClick={onClick}>
            <button className='next'>
                <i className="fa fa-long-arrow-alt-right"></i>
            </button>
        </div>
    );
}

const PrevArrow = (props) => {
    const { onClick } = props;
    return (
        <div className='control-btn' onClick={onClick}>
            <button className='prev'>
                <i className="fa fa-long-arrow-alt-left"></i>
            </button>
        </div>
    );
}

const FlashCard = ({ productItems, addToCart }) => {
    const [count, setCount] = useState(0);
    const [isActive, setIsActive] = useState(false);

    // Định nghĩa imageBaseUrl trong phạm vi hàm FlashCard
    const imageBaseUrl = 'http://localhost:8000/'; // Thay đổi nếu cần

    const handleClick = () => {
        setIsActive((current) => !current);
        setCount(count + 1);
    }

    const handleMinusClick = () => {
        setIsActive((current) => !current);
        setCount(count - 1);
    }

    var settings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 4,
        slidesToScroll: 1,
        pauseOnHover: true,
        nextArrow: <NextArrow />,
        prevArrow: <PrevArrow />,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 3,
                }
            },
            {
                breakpoint: 768,
                settings: {
                    slidesToShow: 2,
                }
            },
            {
                breakpoint: 450,
                settings: {
                    slidesToShow: 1,
                }
            },
        ]
    };

    return (
        <>
            <Slider {...settings}>
                {
                    productItems.map((product) => {
                        return (
                            <div className="box" key={product.id}>
                                <div className="products">
                                    <div className="imgContent">
                                        <span className="discount">{product.discount}% OFF</span>
                                        <div className="imgDiv">
                                            {/* Sử dụng imageBaseUrl để lấy đường dẫn ảnh */}
                                            <img src={`${imageBaseUrl}${product.cover}`} alt={product.name} />
                                        </div>
                                        <div className="products-like">
                                            <label>{count}</label>
                                            <br />
                                            {
                                                isActive ?
                                                    <i className={`fa fa-heart ${isActive ? 'isred' : ''}`}
                                                        onClick={handleMinusClick}></i>
                                                    :
                                                    <i className={`fa-regular fa-heart ${isActive ? 'isred' : ''}`}
                                                        onClick={handleClick}></i>
                                            }
                                        </div>
                                    </div>
                                    <div className="products-details">
                                        <h3>{product.name}</h3>
                                        <div className="rate">
                                            <i className="fa fa-star"></i>
                                            <i className="fa fa-star"></i>
                                            <i className="fa fa-star"></i>
                                            <i className="fa fa-star"></i>
                                            <i className="fa fa-star"></i>
                                        </div>
                                        <div className="price">
                                            <h4>{product.price}.00</h4>
                                            <button onClick={() => addToCart(product)}>
                                                <i className="fa fa-plus"></i>
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )
                    })
                }
            </Slider>
        </>
    )
}

export default FlashCard;
